mapper.select:
select
	*
from
	package_version
where
	1=1

		and package_id=#{package_id}



		and package_version=#{package_version}


		and id=#{version_id}

SELECT utc.`terminalId` terminal_id, t.`type` terminal_type, t.`introduce` terminal_introduce, p.`id` package_id, p.`package_name` package_name, p.`introduce` package_introduce, pv.`id` package_version_id, pv.`package_version` package_version, pv.`introduce` package_version_introduce FROM terminal t, user_terminal_competence utc, package p, package_version pv WHERE utc.`userId`= #{userId} AND utc.`terminalId`=p.`terminal_id` AND p.`id`=pv.`package_id` AND t.`id`=utc.`terminalId`
AND pv.`package_id`=#{package_id}
AND pv.`package_version`=#{package_version}

mapper.update:
update
	package_version
set
	id=#{version_id}

		,package_version=#{package_version}


		,introduce=#{introduce}

where
	id=#{version_id}
